import React, { useEffect } from "react";
import play from "../images/play.png";
import dashboard from "../images/Dashboard.webp";
import AOS from "aos";
import "aos/dist/aos.css"; // Import the AOS styles
const Home = () => {
  useEffect(() => {
    AOS.init();
  }, []); // Ensure it runs only once when the component mounts

  return (
    <div className="overflow-hidden mx-14  bg-customColor overflow-x-hidden flex flex-col items-center">
      <div className="text-center mt-24 mb-2">
        <h1 className=" text-sm border rounded-xl p-1 px-2 text-blue-700 font-semibold bg-white">
          ðŸ‘‹WELCOME TO MANAGE WISE!
        </h1>
      </div>
      <div className="w-full">
        <h1 className="leading-tight text-7xl text-center font-bold">
          Empower your buisness with <br />{" "}
          <span className=" text-red-400">Strategic </span>insights
        </h1>
      </div>
      <div className="mb-10 w-1/2 text-center mt-8 text-xl font-semibold text-gray-500">
        <h1>
          Powerful management platform designed to streamline your business
          operations, boost productivity, and drive success
        </h1>
      </div>
      <div className="flex flex-row ">
        <button className="font-bold mx-3 bg-violet-600 text-white text-xl rounded-2xl px-10 py-3">
          Get Started
        </button>
        <button className="flex flex-row  font-bold mx-3 bg-white text-xl rounded-2xl px-10 py-3 relative">
          Watch Demo
          <span>
            <img src={play} alt="play" className="max-h-5 mt-1 px-2" />
          </span>
        </button>
      </div>
      <div className="m-16" data-aos="zoom-in">
        <img src={dashboard} alt="dashboard-img"></img>
      </div>
    </div>
  );
};

export default Home;
